import React, { useEffect } from 'react';

import Link from 'next/link';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';

import Query from './query';
import SECTIONS_QUERY from '../apollo/queries/sections/sections';


const useFirstStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
}));

// export default props => {
//     const classes = useStyles();

//     return (
//         <div className={classes.root}>
//             <AppBar position="static">
//                 <Toolbar>
//                     <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
//                         <MenuIcon />
//                     </IconButton>

//                     <Button edge="end" color="inherit">Login</Button>
//                 </Toolbar>
//             </AppBar>
//         </div>
//     );
// }

const Sections = props => {
    const classes = useFirstStyles();

    return (
        <>
            {props.data.map(section =>
                <Link href={"/" + section.title.toLowerCase()} key={section.title + "NextLink"}>
                    <a key={section.title + "Link"}>
                        <Typography variant="h6" className={classes.title} key={section.title}>
                            {section.title}
                        </Typography>
                    </a>
                </Link>
            )}
        </>
    );
}


import { makeStyles, withStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';

// const userTools = NavMenuData;
const drawerWidth = 240;

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

const AntTabs = withStyles({
    indicator: {
        //height: '3px',
        //backgroundColor: '#c2b6b6',
        //backgroundImage: 'linear-gradient(315deg, #c2b6b6 0%, #576574 74%)',        
        //borderRadius: '8px'
        display: 'none'
    },
})(Tabs);

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        height: '100%',
    },
    appBar: {
        //Not clipped
        //width: `calc(100% - ${drawerWidth}px)`,
        //marginLeft: drawerWidth,

        //Clipped
        width: '100vw',
        zIndex: theme.zIndex.drawer + 1,
        //backgroundColor: '#442360',
        //Roche Blue
        //backgroundColor: '#0066C9',
        //Tableau Roche Blue
        //backgroundColor: '#004d99',
        backgroundColor: '#3785F3',
        background: 'linear-gradient(90deg, rgba(22,36,63,1) 0%, rgba(55,133,243,1) 50%, rgba(0,102,201,1) 90%)',
        border: 'none'
    },
    toolBar: {
        display: 'flex',
        display: 'flexbox',
        display: 'box',
        alignItems: 'flex-end'
    },
    tabContainer: {
        height: 'inherit',
        minHeight: 'inherit',
    },
    tab: {
        height: 'inherit',
        minHeight: 'inherit',
        '&:hover': {
            color: 'white',
            textDecoration: 'none'
        }
    },
    link: {
        height: 'inherit',
        color: 'white',
        '&:hover': {
            color: 'white',
            textDecoration: 'none'
        }
    },
    tabContainerIE: {
        height: '100%',
        minHeight: '100%',
    },
    tabIE: {
        height: '100%',
        minHeight: '100%',
        '&:hover': {
            color: 'white',
            textDecoration: 'none'
        }
    },
    linkIE: {
        height: '100%',
        color: 'white',
        '&:hover': {
            color: 'white',
            textDecoration: 'none'
        }
    },
}));

export default props => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    // Internet Explorer 6-11
    const isIE = /*@cc_on!@*/false || !!document.documentMode;

    // useEffect(() => {
    //     if (props.route.indexOf(userTools[0].route) > -1 && value !== 0)
    //         setValue(0);
    //     if (props.route.indexOf(userTools[1].route) > -1 && value !== 0)
    //         setValue(0);
    //     if (props.route.indexOf(userTools[2].route) > -1 && value !== 2)
    //         setValue(2);
    // });

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <AppBar position="fixed" className={classes.appBar}>
            <Toolbar className={classes.toolBar}>
                <Query query={SECTIONS_QUERY}>
                    {({ data: { sections } }) => {
                        return <Sections data={sections} />
                    }}
                </Query>
                {/*<AntTabs className={isIE ? classes.tabContainerIE : classes.tabContainer} value={value} onChange={handleChange} aria-label="simple tabs example">*/}
                    {/*userTools.map((tool, index) => (
                        <Tab className={isIE ? classes.tabIE : classes.tab} component={Link} to={tool.route} label={tool.name} {...a11yProps(index)} />
                    ))*/}
                    {/*<Tab className={isIE ? classes.tabIE : classes.tab} component={Link} to={userTools[1].route} label={userTools[1].name} {...a11yProps(0)} />*/}
                {/*</AntTabs>*/}
            </Toolbar>
        </AppBar>
    );
}
